void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xx -.5);
	//vec2 uv = (fragCoord.xy / iResolution.xx - 0.5)ï¼›
	 float c = 0.02;
         uv.y+=.22;
	//uv*= abs(cos(iTime) + 1.);
    float iiTime = iTime * 0.1;
	uv*= mat2(sin(iiTime),cos(iiTime),-cos(iiTime),sin(iiTime));
	vec3 p = vec3(uv,1.0);
	c = .3/length(uv);
	vec3 mask = fract(p*10.);
	vec3 mask1 = fract(p*100.);
	mask*= vec3(.3,0.,0.);
	if(mask.x > .05 && mask.y > .05) {
		mask = vec3(0.);
	} else {
		mask = vec3(1.)*c;
	}
	if(mask1.x > .05 && mask1.y > .05) {
		mask1 = vec3(0.);
	} else {
		mask1 = vec3(1.)*c;
	}
	mask += mask1;
	vec3 col = mask * abs(sin(iiTime))* abs(sin(iiTime)) * vec3(p +.3);

    // Output to screen
    fragColor = vec4(cos(iTime * col) * sin(iTime * col) + col,1.0);
}
