void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    int doubleLineWidth = int(iResolution.x) / 10;
    int lineWidth = doubleLineWidth / 2;
    int x = int(fragCoord.x);
    int s = doubleLineWidth - iFrame % doubleLineWidth;
    if ((x + s) % doubleLineWidth >= 0 && (x + s) % doubleLineWidth <= lineWidth)
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    else
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
}
