#define PI 3.141592653

vec3 colMap(float v) {

	v=mod(v, PI+1.3)-.8;	
    return vec3(
		sin(sin(v-1.6)),
		sin(sin(v)),
		sin(sin(v+.8))
	);

} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float bass = texture(iChannel0,vec2(0.2)).r;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;    
    vec4 last = texture(iChannel1,uv);
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    float pxl = 2./iResolution.x;

    // Time varying pixel color
    float d = length(uv)/(1.+bass);
    float a = atan(uv.y,uv.x);
    float b = 0.;
    d *= (cos(a*6.+iTime*5.*d*pxl*sin(iTime))+cos(a*3.+1.+iTime)+.5*cos(a*12.+1.+iTime))*.1 + 1.;

    //for(float i=1.; i<26.; i+=1.)
        b += smoothstep(0.,pxl,sin(d*140.+10.*iTime+(1.+sin(iTime))*10.));

    vec3 col = vec3(b*colMap(sin(iTime)*d*2.-(iTime + (1.+sin(iTime)))));

    // Output to screen

    float smear = .93;
    fragColor = vec4(colMap(col.r+.1)+colMap(col.b-.1),1.0)*1.5*(1.-smear) + smear*last;
}
