//https://www.shadertoy.com/view/lsXGzM reference

float speed=2.0;
float n=2.0;
float smoothsteporiginsize=0.5;
float smoothstepsize=0.65;
float N21 (vec2 p){
	float d = fract(sin(p.x*110.+(8.21-p.y)*331.)*1218.);
    return d;
}

float Noise2D(vec2 uv){
    vec2 st = fract(uv);
    vec2 id = floor(uv);
    st = st*st*(3.0-2.0*st);
    float c=mix(mix(N21(id),N21(id+vec2(1.0,0.0)),st.x),mix(N21(id+vec2(0.0,1.0)),N21(id+vec2(1.0,1.0)),st.x),st.y);
	return c;
}


float blob(float x,float y,float fx,float fy,float size){
   float xx = x+abs(iMouse.x/ iResolution.x-0.5)*sin(iTime/speed*size+fx)*size*13.;
   float yy = y+abs(iMouse.x/ iResolution.x-0.5)*cos(iTime/speed*size+fy)*size*13.;
   float value=xx*xx+yy*yy;
   return min(690.,20./value*(iTime+10.0))/500.;
   //return min(360.,pow(20.0/value,1.0))/30.;
}


