void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // sites
    vec2 s[7] = vec2[7] (vec2( 0.2*cos(3.*iTime)+0.0, 0.2*sin(   iTime)+0.3),
                         vec2( 0.1*cos(   iTime)+0.0, 0.1*sin(6.*iTime)-0.2),
                         vec2( 0.3*cos(   iTime)-0.1, 0.3*sin(4.*iTime)+0.2),
                         vec2(-0.2*cos(7.*iTime)-0.3,-0.2*sin(   iTime)-0.5),
                         vec2(-0.3*cos(   iTime)+1.0,-0.3*sin(   iTime)+0.5),
                         vec2(-0.1*cos(   iTime)-1.0,-0.1*sin(5.*iTime)+0.5),
                         vec2(-0.9*cos(2.*iTime)+0.0,-0.9*sin(   iTime)+0.0));
    // colors
    vec3 c[8] = vec3[8] (vec3(0, 0, 0),
                         vec3(0, 0, 1),
                         vec3(0, 1, 0),
                         vec3(0, 1, 1),
                         vec3(1, 0, 0),
                         vec3(1, 0, 1),
                         vec3(1, 1, 0),
                         vec3(1, 1, 1));
    // Normalized pixel coordinates 
    float aspect_ratio = iResolution.x/iResolution.y;
    vec2 uv = 2.*fragCoord/iResolution.y - vec2(aspect_ratio, 1.);  
    //vec2 uv = 2.*(fragCoord/iResolution.y) - 1.0;

    int i,k = 1;
    float n, d = dot(s[0]-uv, s[0]-uv);
    for (i=1;i<7;i++)
    {
        n = dot(s[i]-uv, s[i]-uv);
        if (n < d)
        {
            d = n;
            k = i+1;
        }
        if (d < 0.0001)
            k = 0;
    }
    // Output to screen
    fragColor = vec4(c[k],1.0);
}
