void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float cambioAng = 0.;

    for(int i=0;i<4;i++)
    {
        float ang = -0.6 + cambioAng;
        mat2 M = mat2(cos(ang), -sin(ang), sin (ang), cos(ang));
        vec2 uvTentaculo = (2.0 * fragCoord - iResolution.xy) / iResolution.xy;
        uvTentaculo *=M;
        uvTentaculo.x += iTime;
        float h = smoothstep(0.,uvTentaculo.y + 0.1 * sin (4.0 * uvTentaculo.x), .05 );
        vec3 colorTentaculo = vec3 (h);
        colorTentaculo *= vec3(0.0,1.0,0.0);
        col+=colorTentaculo;
        cambioAng+=0.4;
    }

    vec2 uvOjo = (2.0 * fragCoord - iResolution.xy) / iResolution.xy;
    float ojo = length(uvOjo);
    //ojo = step(0.4,ojo);
    //ojo = 1.-ojo;
    ojo = smoothstep(0.35,ojo,0.4);
    ojo += step(0.6,ojo);
    vec3 colorOjo = vec3 (ojo);
    colorOjo *= vec3(0.0,1.0,0.0);

    vec2 uvRelleno = (2.0 * fragCoord - iResolution.xy) / iResolution.xy;
    float relleno = length(uvRelleno);
    relleno = step(0.35,relleno);
    relleno = 1.-relleno;
    //relleno = smoothstep(0.35,relleno,0.4);
    //relleno += step(0.6,relleno);
    vec3 colorRelleno = vec3 (relleno);
    colorRelleno *= vec3(0.1,1.0,0.0);

    vec2 uvPupila = (2.0 * fragCoord - iResolution.xy)/ iResolution.y;
    uvPupila.x += (fragCoord.x-iMouse.x)/ iResolution.x;//.5;
    float Pupila = length(uvPupila*2.);
    Pupila = smoothstep(0.4,Pupila,2.5);
    Pupila = 1.-Pupila;
    //ojo2-=step(ojo2,0.1);
    vec3 colorPupila = vec3 (Pupila);
    colorPupila *= vec3(1.5,.5,.5);


    vec2 uvIris = (2.0 * fragCoord - iResolution.xy)/ iResolution.y;
    uvIris.x += ((fragCoord.x-iMouse.x)/ iResolution.x*1.24);//.5;
    float Iris = length(uvIris*7.);
    Iris = smoothstep(0.4,Iris,10.);
    Iris = 1.-Iris;
    vec3 colorIris = vec3 (Iris);
    colorIris *= vec3(1.,1.,1.);

    //vec3 col = colorOjo + colorTentaculo + colorTentaculo2;

    //col *= vec3(0.0,1.0,0.0);
    vec3 exterior = vec3(1.,10000.,1.);
    exterior = mix(colorPupila,colorIris,exterior);


    colorRelleno+=vec3(0.1,0.,0.1);

    col+=colorOjo+colorRelleno+exterior;


    uvRelleno = (500.0 * fragCoord) / iResolution.x;
    col += min(1.,exterior.y) * texture(iChannel0, uvRelleno).xyz;


    vec2 uvWebcam = fragCoord / iResolution.xy;
        float movx = .1*(iMouse.x - iResolution.x) / iResolution.x;


    //col += smoothstep(.5, 1., colorOjo.x+colorRelleno.y) * texture(iChannel1, uvWebcam+movx).xyz;  

    col += smoothstep(.5, 1., colorRelleno.y) * texture(iChannel2, uvWebcam+movx).xyz/2.;


    fragColor = vec4(col,1.0);
}
