renderpass.code:
float noise(vec2 p)
{
	float s = texture(iChannel1,vec2(1.,2.*cos(iTime))*iTime*10. + p*4.).x;
	s *= s;
	return s;
}

float ramp(float y, float start, float end)
{
	float inside = step(start,y) - step(end,y);
	float fact = (y-start)/(end-start)*inside;
	return (1.-fact) * inside;
}

float stripes(vec2 uv)
{
	float noi = noise(uv*vec2(0.1,1.) + vec2(1.,3.));
	return ramp(mod(uv.y*1. + iTime/2.+sin(iTime + sin(iTime*0.63)),1.),0.5,0.6)*noi;
}

vec3 getVideo(vec2 uv)
{
	vec2 look = uv;
	vec3 video = vec3(texture(iChannel0,look));
	return video;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 video = getVideo(uv);

	video += stripes(uv);

	fragColor = vec4(video,1.0);
}
