vec2 map(in vec2 z, in vec2 c) {
    return vec2(z.x*z.x - z.y*z.y + c.x, 2.*z.x*z.y + c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 zoomLoc = vec2(.45, .202);
    // for the Mandelbrot set try
    // vec2 zoomLoc = vec2(.8301,.20);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 5.*(fragCoord - .5*iResolution.xy)/(iResolution.x*exp(mod(iTime,15.)))-zoomLoc;

    vec2 c = vec2(-0.12256,0.74486);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 frag = vec3(0,0,0);

    // for the Mandelbrot set
    vec2 imof0 = vec2(0,0);

    for (int i = 0; i < 100; i++) {
        uv = map(uv, c);
        // for the Mandelbrot set replace the above with
        // imof0 = map(imof0, uv);

        if(length(uv) > 10.) break;
        // for the Mandelbrot set replace the above with
        // if(length(imof0) > 10.) break;
        frag += 0.01 * col;
    }

    // Output to screen
    fragColor = vec4(frag,1.0);
}
