//const float percent = 0.9;

const float timeTaken = 3.0; //s



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Output to screen
   vec3 col = vec3(0.0,0.0,0.0);

   vec2 xy = fragCoord.xy;
   xy.x = xy.x/iResolution.x;
   xy.y = xy.y/iResolution.y;

   vec4 vid1 = texture(iChannel0,xy);
   vec4 vid2 = texture(iChannel1,xy);


   float percent = 0.0;

   float initialTime = 5.0;

   if(iTime>initialTime){
    percent = (iTime-initialTime)/timeTaken; 
   }


    if(percent>=0.0 && percent<=1.0)
    col = vid1.rgb*(1.0-percent) + vid2.rgb*(percent); 
    else
    col = vid2.rgb;


    // Output to screen
    fragColor = vec4(col,1.0);
}
