vec3 voidFluid(vec3 target) {
    vec3 monoChrome = vec3(0.21 * target.x + 0.71 * target.y + 0.07 * target.z);
    return (monoChrome * monoChrome) / (vec3(1) - monoChrome * vec3(1.0, 0.780392157, 0.388235294));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(voidFluid(texture(iChannel0, fragCoord / iResolution.xy).xyz), 1.0);
}
