#define N_PARTICLES 60.

vec2 R12(float t) 
{
    float x = fract(sin(t*674.4)*453.2);
    float y = fract(sin((t+x)*741.3)*263.2);
    return vec2(x,y);
}

float R21(vec2 p) 
{
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

float Explosion(vec2 uv, float ft) 
{
    float sparks = 0.;
    for (float i = 0.; i < N_PARTICLES; i ++)
    {
        vec2 direction = R12(i+1.)-.5;

        float dist = length(uv-direction*ft);
        float brigthness = .0005;

        brigthness *= sin(ft*10.+i)*.5+.5;

        sparks += brigthness/dist;

        sparks *= smoothstep(1., .1, dist);
    }

    return sparks;
}

vec3 FireWork(vec2 uv)
{
    float flt = floor(iTime);
    float frt = fract(iTime);

    uv = uv - flt;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    vec2 cid = vec2(0.);
    vec3 col = vec3(0.);

    vec3 color = sin(vec3(frt, uv.x, uv.y))*.56+.78;

    for (float y = -1.; y <= 1.; y ++)
    {
        for(float x = -1.; x <= 1.; x++)
        {
            vec2 offset = vec2(x,y);

            float rf = R21(id + offset);

            float explosion = Explosion(gv-offset-rf, fract(iTime-rf));

            float depth = 3. * rf;

            col += explosion*depth*color;

        }
    }

    col.rgb += color*.03;
    col /= frt;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord- .5*iResolution.xy)/iResolution.y;

    uv *= 2.;

    vec3 col = vec3(0.);

    vec3 fireworks = FireWork(uv);

    col += fireworks;

    fragColor = vec4(col,1.0);
}
