float PI = 3.14;

vec4 funya(vec2 uv){
    vec3 destColor = vec3(0.0);
    float num = .15*iTime < 40.0 ? .15*iTime : 40.0;
    for(float i = 0.0; i < num; i++){
        vec2 q = uv + .7*vec2(cos( ( i/num ) * PI + .179*iTime), sin( ( i / num ) * PI + .199*iTime));
        float theta  = atan(q.y,q.x);
        destColor.yz += cos(theta*30.0);
    }
    return vec4(destColor, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 step_uv = floor(uv * 5.0) / 5.0;
    vec4 sound = texture(iChannel1, vec2(.001,0.0));
    vec4 s2 = texture(iChannel1, vec2(.1,0.0));
    fragColor = funya(uv-.5+ vec2 (.3*(sound.x -.5) + .1*(s2.x - .5) , .0));
}
