#define ITERATIONS 1000

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 c = vec2(cos(iTime * 0.1), sin(iTime * 0.1));
    c = vec2(2. * c.x - c.x*c.x + c.y * c.y, 2. * c.y - c.x * c.y);
    c /= 4.;

    fragCoord -= iResolution.xy/2.;
    fragCoord /= iResolution.y;
    fragCoord *= 2.;

    vec2 z = fragCoord;

    int i = 0;
    while (i < ITERATIONS) {
        z = vec2(z.x*z.x - z.y*z.y + c.x, 2. * z.x * z.y + c.y);
        i++;
        if (length(z) > 2.) break;
    }

    float t = float(i)/float(ITERATIONS);

    fragColor = 
      vec4(2.0, 0., 0., 0.) / (1.0 + pow(2.0, pow(2.0 - 100.0* t, 2.0)))
    + vec4(0., 0., 15., 1.) * (t * pow(1. - t, 9.0)) 
    + vec4(0., 20., 0., 1.) * (t * t * (1. - t)) 
    + vec4(1., 1., 1., 1.)  * t;
}
