void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

vec2 hash22(vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    if (fragCoord.y < 2. && iMouse.z < 0.001) {
        fragColor = vec4(1);
        return;
    }

    vec2 rand = floor(hash22(fragCoord * 999. + iTime) * 3.);

    vec2 offset = vec2(rand.x - 1., -rand.y);
    vec4 neighbor = texelFetch(iChannel0, ivec2(mod(fragCoord + offset, iResolution.xy)), 0);
    vec3 color = neighbor.rgb - vec3(0.0025, 0.0035, 0.005);

    fragColor = vec4(color, 1.);
}
