// Tunnel sdf. 
float sdTunnel( in vec2 p, in vec2 s )
{
    return max(-p.y, length(p-vec2(0,clamp(p.y, -s.y-s.x, s.y)))-s.x );
}

// Based on iq's 2d sdf shader.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y+1.;

    vec2 s = vec2(max(0.,sin(iTime))*0.5+.25, max(0.,-sin(iTime))*.5+.5);
    if( iMouse.z>0.001 )
    {
        s=m;
    }
	float d = sdTunnel(p+vec2(0,.5), s);

	// coloring
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

	fragColor = vec4(col,1.0);
}
