// params: uv coordinates, the size of the heart, the offset from the origin
float drawHeart(vec2 uv, float size, vec2 offset) {
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;
    float group = pow(x,2.) + pow(y,2.) - size;
    float d = pow(group,3.) - pow(x,2.) * pow(y,3.);

    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  2. * fragCoord / iResolution.xy -1.;
    uv.x *= iResolution.x /iResolution.y; // fix aspect ratio

    vec3 bgColor = vec3(3.,.8,0); // set background color
    vec3 heartColor = vec3(0, .5, 1.);

    // set the offset to move the heart clockwise 
    // -- increase 1 to increase the speed
    // -- increase/decrease 0.4 to set the radius of the circle
    vec2 offset = vec2(sin(iTime * 1.) * 0.4, cos(iTime * 1.) * 0.4);
    float heart = drawHeart(uv, 0.2, offset);

    vec3 col = mix(heartColor, bgColor, step(0., heart));
    fragColor = vec4(col,1.0); // Output to screen
}
