float GetBallValue(vec3 ball, vec2 uv) {
    return ball.z/((ball.x-uv.x)*(ball.x-uv.x) + ((ball.y-uv.y)*(ball.y-uv.y)));//distance(ball.xy, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ball1 = vec3(0.9 + 0.5*cos(0.5*iTime),  0.5 + 0.3*sin(0.5*iTime), 0.02);
    vec3 ball2 = vec3(0.9 + 0.5*cos(0.37*iTime), 0.5 - 0.3*sin(0.2*iTime), 0.02);
    vec3 ball3 = vec3(0.9 + 0.5*cos(0.43*iTime), 0.5 + 0.3*sin(0.24*iTime), 0.01);

    float val
        = GetBallValue(ball1, uv)
        + GetBallValue(ball2, uv)
        + GetBallValue(ball3, uv);

    fragColor = vec4(0.1, 0.1, 0.1, 1);
    fragColor += smoothstep(0.95, 1.0, val) * 1.4 * vec4(0.5 + 0.5*cos(0.5*iTime+uv.xyx+vec3(0.0,2.0,4.0)), 0);
}
