/* Stanislav Pidhorskyi - 2022 
Inspired by "Fractal Tiling" by Inigo Quilez https://www.shadertoy.com/view/Ml2GWy
Uses hash from "Hash without Sine" by David Hoskins https://www.shadertoy.com/view/4djSRW */

#define r iResolution
#define f(v,e) for(int v=0;v<e;++v)
#define m(a) mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage(out vec4 o,vec2 a){
    float g=iTime,s=0.,k;
    f(c,3){s+=.005;
    f(l,5){g+=.03;k=-2.;vec2 i=(a-.5*r.xy)/r.y*2./(abs(sin(g))+1.);
    f(j,8){
        f(t,2){i=(abs(i+s)-s)*m(.1+g*.1)*2.;}
        vec4 w=fract(floor(i).xyxy*vec4(.1031,.103,.0973,.1099));
        w+=dot(w,w.wzxy+33.33)+iTime*.001;
        w=fract((w.xxyz+w.yzzw)*w.zywx);
        o[c]+=w[c]*w.w*exp(k);k-=.2;
    }}}
    o=pow(o*.7-.02,vec4(1.5,2.2,3.,0.));
}
