void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv += texture(iChannel0, uv + vec2(iTime * 0.2, iTime * 0.1)).rr * 0.01 - vec2(iTime * 0.04, iTime * -0.02);

    //uv += vec2(cos(uv.y * 5.0 + iTime)*0.5 + 0.5, sin(uv.x * 5.0 + iTime)*0.5 + 0.5);

    vec3 col = texture(iChannel1, uv * 0.65).rgb;

    col *= vec3(0.8, 0.8, 1.0);

    float add = texture(iChannel0, uv + vec2(iTime * 0.3, iTime * 0.2) + vec2(texture(iChannel1 , uv).r * 0.04, texture(iChannel1 , uv).r * -0.02)).r;
    add = step(0.95, add);
    col += add;

    // Output to screen
    fragColor = vec4(col,1.0);
}
