// variant from https://shadertoy.com/view/ft3fDM


void mainImage( out vec4 O, vec2 U )
{
    int T = int(iTime)%3;
    float w, t=0., d2,  
         dof = 38.*( iMouse.z > 0. ? iMouse.x : U.x ) / R.x, // depth of field → current radius
         dens = 8.,                                          // sampling density
         N = clamp( dens*dof*dof , 8., 1e4 );                // sampling ~ disk area
    O-=O;
    for( float i=0.; i < N; i++ ) {
        vec2 dP = vec2(cos(i*2.4),sin(i*2.4)) * sqrt(i/N);   // Vogel disc sampling ( 2.39996 )
        vec4 C = texture(iChannel0,(U+dof * dP) / R );       // Bokeh sampling of the image
        d2 = dot(dP,dP);
        t += w =   T==0 ? 1.                                 // disc profile
                 : T==1 ? exp( -.5*d2 *6.)                   // Gaussian profile ( cut at 2.5 sigma )
                 :        step( abs( d2 - .5 ) , .1 );       // ring
        O += w * C;
    }

    O = pow( O/t, vec4(1./2.2));                             // to sRGB
}

void mainImage( out vec4 O, vec2 U )
{
    if ( U.y/H < .1 ) { O = 1e2*vec4( mod(U-.5,32.)==vec2(16) ); return; } // impulse ( to show filter as impulse response )

    // net pattern
    U = (U+vec2(-U.y,U.x))/1.4; U.x*=2.;  U = (U-vec2(-U.y,U.x))/1.4;
    U = abs( fract(S*U/H)-.5 );
    O = vec4( smoothstep( 1.5*S/H, 0., min(U.x,U.y)-.1 ) );
}
