// remix: https://www.shadertoy.com/view/ldX3zr
vec2 center = vec2(0.5, 0.5);
float speed = 0.025;

float getAmp(float frequency) { // from: https://www.shadertoy.com/view/4ljGD1
  return texture(iChannel0, vec2(frequency / 512.0, 0)).x;
}

float getWeight(float f) { // from: https://www.shadertoy.com/view/4ljGD1
  return (+getAmp(f - 2.0) + getAmp(f - 1.0) + getAmp(f + 2.0) + getAmp(f + 1.0) + getAmp(f)) / 5.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float invAr = iResolution.y / iResolution.x;

  vec2 uv = fragCoord.xy / iResolution.xy;

  float weight = getWeight(uv.x * cos(iTime) + uv.x * sin(iTime));

  vec3 col = vec3(.8, .4, 0.5 + 0.5 * sin(iTime));

  vec3 texcol;

  float x = center.x - uv.x;
  float y = (center.y - uv.y) * invAr;

	// float r = -sqrt(x*x + y*y); // uncoment this line to symmetric ripples
  float r = pow(x * x + y * y, weight * 1.915);
  float z = 1.0 + 0.5 * sin((r + iTime * speed) / 0.013);

  texcol.x = z;
  texcol.y = z;
  texcol.z = z;

  fragColor = vec4(clamp(col * texcol, .0, 1.0), 1.0);
}
