//Zoom Swap Illusion by eiffie
//While zooming out the green square in the center is replaced by a red diamond.
#define time (iTime-2.)
void mainImage( out vec4 O, in vec2 U ){
    //U-=.5;//uncomment to break illusion
    vec2 uv = (2.*U-iResolution.xy)/iResolution.x;
    vec2 p=abs(2.*fract(uv*time*15.)-1.);//tile and zoom out
    float g=min(p.x,p.y)-.25;            //green squares
    float r=1.-p.x-p.y;                  //red diamonds
    O = vec4(r,g,0,0)*(1.-length(uv));
}

vec2 mainSound( int samp, float time )
{//ByteBeats
  int t=int(time*88200.+.5);
  vec2 v=vec2((t*(t>>9 & t>>14))%256,((t+32)*(t>>8 & t>>12))%256)/256.-.5;
  return sin(v*6.283)*sin(vec2(time,time+1.57))*clamp(time*.025,.0,.25);
}
