// MIT

float GloomThreshold = 0.25f;
float GloomIntensity = 2.0f; // 0-10
float BaseIntensity = 1.0f; // 0-10
float GloomSaturation = 1.0f; // 0-100
float BaseSaturation = 2.0f; // 0-100


vec4 AdjustSaturation(vec4 color, float saturation)
{
    float grey = dot(color.rgb, vec3(0.3f, 0.59f, 0.11f));
    return mix(vec4(grey), color, saturation);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0,uv);

    vec4 base = 1.0 - col;
    vec4 gloom = clamp((base - GloomThreshold) / (1.0 - GloomThreshold),0.,1.);    

    // Adjust color saturation and intensity.
    gloom = AdjustSaturation(gloom, GloomSaturation) * GloomIntensity;
    base = AdjustSaturation(base, BaseSaturation) * BaseIntensity;

    // Darken down the base image in areas where there is a lot of bloom,
    // to prevent things looking excessively burned-out.
    base *= (1. - clamp(gloom,0.,1.));

    // Combine the two images.
    fragColor = 1.0 - (base + gloom);
}
