void mainImage(out vec4 f, in vec2 c) {
    vec2 s = (c / iResolution.xy) * 6.2831;


    float K = 3. * sin(iTime / 10.);
    float x = s.x, y = s.y;

    for (int i = 0; i < 50; i ++) {
        y = mod(y + K * sin(x), 6.2831);
        x = mod(x + y, 6.2831);
    }
    f = vec4(abs(y - s.y));
}
