//gaussian_kernel_one_stdev
float[] gk1s = float[] (
        0.003765,0.015019,0.023792,0.015019,0.003765,
        0.015019,0.059912,0.094907,0.059912,0.015019,
        0.023792,0.094907,0.150342,0.094907,0.023792,
        0.015019,0.059912,0.094907,0.059912,0.015019,
        0.003765,0.015019,0.023792,0.015019,0.003765 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    //size of one texel in iChannel0
    vec2 texel = 1./iResolution.xy;

    vec4 total_color;

    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }


    fragColor = total_color;
}

//philip.bertani@gmail.com

vec3  ro, ifs_color, light_direction;
float max_iter, normal_mult, norm_dist_expand;

float params[9] = float[9] (-.7,-.2,-.1,.8 ,0.1, 1.4, 7.,2.5,2.7);

mat3 rot_xz_yz(float a1, float a2) {
    //rotation matrices - can't live with'em, can't live without'em
    float c1=cos(a1),c2=cos(a2),s1=sin(a1),s2=sin(a2);
    return mat3(    c1, 0.,    -s1,
                -s2*s1, c2, -s2*c1,
                 c2*s1, s2,  c2*c1 );
}


float mbox (vec3 p)
{
    ifs_color = vec3(0.);
    vec4 offset=vec4(p,1.), v=offset;

    float n=0.;
    for (; n<200.;n++) {

        v.xyz = clamp (v.xyz, -1.-params[0], 1.+params[0]) * (2.+params[1]) 
                - (1.+params[2])*v.xyz;


        v = v * params[3] / clamp (dot (v.xyz, v.xyz), (.2+params[4])-params[5], 1.+params[5]) + offset;

        if (v.x*v.y > 0. ) ifs_color.x ++;
        if (v.y*v.z > 0. ) ifs_color.y ++;
        if (v.z*v.x > 0. ) ifs_color.z ++;  

        if ( n > max_iter ) break;   //allow for variable # of iterations
    }

    ifs_color /= n;

    return length (v.xyz) / v.w ;
}

float all_objects(vec3 p) {
    //the second part of the max keeps us inside a hollow sphere so we can see stuff
    //when we are buried inside with no clear path
    return max( mbox(p), -(length(p-ro)-.0002)  );
}


vec3 gradient(vec3 p) {


    vec2 dpn = vec2(1.,-1.);
    vec2 dp  = (.001*normal_mult + norm_dist_expand) * dpn; 

    vec3 df = dpn.xxx * all_objects(p+dp.xxx) +
              dpn.yyx * all_objects(p+dp.yyx) +
              dpn.xyy * all_objects(p+dp.xyy) +
              dpn.yxy * all_objects(p+dp.yxy);

    return normalize(df); 

}


vec3 color(vec3 p, vec3 rd, float dist, float totdist) {

    float t2 = totdist*totdist;

    norm_dist_expand = 500.*t2*t2; //make the far away normals blurry

    vec3 nv = gradient(p);

    vec3 color = vec3(  ifs_color.x*cos(ifs_color.x*params[6]),
                        ifs_color.y*cos(ifs_color.y*params[7]),
                        ifs_color.z*cos(ifs_color.z*params[8]) );


    color = normalize(color);

    color = 1.  - exp(-color);


    color = color * 1.4* max (0., dot (nv, -light_direction))  
            +  .6*pow ( max (0., dot (-light_direction, reflect (rd, nv))), 8. );


    color *= exp(-t2);
    return clamp(color,0.,1.);
}

void mainImage( out vec4 rgba, in vec2 xy )
{

    float tt = 50. + mod(iTime,20.);

    xy = (2.*xy - iResolution.xy)/iResolution.y;

    ro = vec3(-.003,0.004,-.1);
    max_iter = 20.;
    normal_mult = .01;

    mat3 rot = rot_xz_yz(1.5-tt/63.,-.6+tt/70.);
    vec3 rd = rot* normalize(vec3(xy,-1.6));

    ro -= rot[2] * (tt+30.)/6e4;
    light_direction = normalize(-rot[2]+vec3(-.4,0.,-.6) ); 

    vec3  p=ro;
    float dist,totdist;
    float eps = 1e-6, hit_threshold=eps;

    float distfac = 50.;
    vec3  rgb = vec3(0.);

    for (float f=0.; f<100.; f++) {

        dist = all_objects(p);

        if ( dist < hit_threshold || totdist > 3. ) { rgb = color(p, rd, dist, totdist); break; }

        float stepsize = dist;
        p += rd*stepsize;

        totdist += stepsize;

        hit_threshold = eps*(1.+totdist*distfac);

    }


    rgba = vec4( rgb,1.);
}
