void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float slide_spd = 0.2;
    float ratio = iResolution.y/iResolution.x;

    vec2 CG = vec2(0.5, 1.0);

    float dev = (pow(0.5-uv.x, 2.0));
    uv.y += (dev*2.0)*0.8;
    uv.y -= 0.1;

    float test = abs((uv.x-0.5)*2.0);
    uv.x = test;

    vec2 slide = vec2(iTime*slide_spd, 0.0);
    vec4 frag_col = texture(iChannel0, uv+slide);

    if (uv != clamp(uv, 0.0, 1.0)) {
        frag_col = vec4(0.0);
    }


    //frag_col.rgb = vec3(test);

    fragColor = frag_col;
}
