bool photonEjector(float width, float currX, float rx, float dotSpace, float times){
bool ans=false;
for(float i=0.0;i<=times;i=i+1.0){
if(rx>currX+dotSpace*i && rx<currX+width+dotSpace*i){
ans=true;
}
}
return ans;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec4 color = texture( iChannel1, (fragCoord.xy/iResolution.xy));
   vec2 r = fragCoord.xy/iResolution.xy;
   vec3 col=vec3(color.r,color.g,color.b);
   vec3 black=vec3((color.r+color.g+color.b)/3.0);
   float width=0.009;
   float dotSpace=0.02;
   float currX=1.0*abs(sin(r.x)*sin(iTime/5.0));
   float height=0.08+0.005*abs(sin(r.y*sin(iTime)));
   float count = 5.0+10.0*abs(sin(r.y)*sin(iTime)*20.0*cos(iTime));

   for(float q=0.0;q<=1.0;q+=0.1){
   if(r.y<q+height && r.y>q && photonEjector(width, currX, r.x, dotSpace, count)){
   color=texture( iChannel1, fragCoord.xy/iResolution.xy + vec2(0.2,0.2));
   }
   }
   fragColor = color;
}
