#define NOISE iChannel0
#define BACKGROUND iChannel1

vec2 pixelate(vec2 pos, vec2 res) {
    return floor(pos * res) / res;
}

// returns a value between -1.0 and 1.0
float noise(vec2 pos, float scale) {
    // params
    const int iterations = 10;
    float amplitude = 0.5;

    // internal
    float value = 0.0;
    float max_amp = 0.0;

    pos /= scale;

    for (int i = 0; i < iterations; ++i) {
        value += texture(NOISE, pos).x * amplitude;

        pos *= 2.0;
        max_amp += amplitude;
        amplitude *= amplitude;
    }

    return 2.0 * (value / max_amp) - 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    #define WARP 0.25
    #define WARP_SPEED 0.01
    vec2 RESOLUTION = iResolution.xy / 4.0;

    fragCoord /= iResolution.xy;
    //fragCoord = pixelate(fragCoord, RESOLUTION);

    float time = iTime * WARP_SPEED;

    float noise1 = noise(fragCoord + vec2(-time, time), 30.0);
    float noise2 = noise(fragCoord + vec2(time, -time), 30.0);

    vec2 warp_dir = vec2(noise1, noise2);

    fragColor = texture(BACKGROUND, fragCoord + warp_dir * vec2(WARP));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragCoord /= iResolution.xy;

    float value = fract((distance(vec2(0.5), fragCoord) - (iTime * 0.01)) * 8.0);
    float angle = atan(fragCoord.y - 0.5, fragCoord.x - 0.5);

    vec2 pos = vec2(cos(angle), sin(angle)) * value;

    vec3 c = texture(iChannel0, pos).xyz;

    fragColor = vec4(pow(c, vec3(3.0)), 1.0);
}
