void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 color = vec3(0.0, 0.4 + uv.y*0.05, 0.7 -uv.y*0.2);

    for (int i; i<5; i++) { //try modifying the i value, I don't really understand why it happened
    vec2 pos = vec2(
                    mod(32.4325+7.94*sin(float(i)*32.0489), uv.x),
                    mod(iTime*sin(24.9372*float(i))+35.902*float(i),uv.y)
                    );
    float rad = 0.056 + pow(0.6*(.4325+.325*sin(float(i)*26.439)),3.0);
    float dis = length(uv+pos);

    vec3 col_bubble = vec3(1.0,1.0,1.0);

    float bubble = length(uv+pos)/rad;

    color += col_bubble*(1.0-smoothstep(rad*0.95, rad,dis))*bubble;
    }
    fragColor = vec4(color, 1.0);
}
