mat2 rot(float a)
{
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

mat2 mirror()
{
    return mat2(1,0,0,-1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5);
    uv*=10.;

    for(int i = 0; i<35; i++)
    {
        uv = abs(uv)-2.;
        uv*=rot(iTime*0.01+sin(float(i)*0.3));
        uv=abs(uv);
        uv*=1.1;
    }
    float pct = smoothstep(1.8,2.1,distance(uv, vec2(0.)));

    // Time varying pixel color
    vec3 col = vec3(1,0.3,0.3)*pct+(1.-pct)*vec3(1,1,1);//vec3(step(distance(uv, vec2(0.)),2.));

    // Output to screen
    fragColor = vec4(col,1.0);
}
