#define TAU 6.2831853071795862 // 2*PI
#define MIN_FREQ 1.0           // in Hertz

vec2 sine_distortion(
    float u,
    float v,
    float uAmplitude,
    float vAmplitude,
    float uFrequency,
    float vFrequency)
{
    return vec2(
        u + uAmplitude * sin((TAU * v) * uFrequency),
        v + vAmplitude * sin((TAU * u) * vFrequency));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float uAmpScale = (iMouse.x / iResolution.x) * 0.01;
    float vAmpScale = (iMouse.x / iResolution.x) * 0.01;

    float uAmplitude = 0.001 + (iMouse.x / iResolution.x) * uAmpScale;
    float vAmplitude = 0.001 + (iMouse.y / iResolution.y) * vAmpScale;

    float frequency = (MIN_FREQ + 1.0 + sin(iTime * 0.7)) * 10.0;

    uv = 
        sine_distortion(
            uv.x,
            uv.y,
            uAmplitude,
            vAmplitude,
            frequency,
            frequency * 0.9);


    // Output to screen
    fragColor = texture(iChannel1, uv);
}
