void mainImage(out vec4 c,in vec2 o){
  c=vec4(0);o-=iResolution.xy/2.0;
  float t=iTime,dt=256.0;vec2 x,y,z;
  for (float i=0.0;i<=16.0;i+=0.25){t=iTime/16.0+i*dt;
      x=vec2(cos(t*1.0)*120.0,sin(t*1.0)*120.0);
      y=vec2(cos(t*10.0)*33.0,sin(t*10.0)*33.0);
      z=vec2(cos(t*15.0)*50.0,sin(t*15.0)*50.0);
      c+=vec4(0.0,0.6,0.8,1.0)*vec4(length(o)/length(x+y+z-o)/16.0)/(i+1.0)/2.0-vec4(length(x+y+z)<length(o)/16.0)/(i+1.0);
  }
}
