#define pi 3.14159

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0. * iResolution.xy) / iResolution.xy;

    float res = iResolution.x/ iResolution.y;

    vec3 col = vec3(0);

    float drop = clamp(iTime / 11., 0., 1.); step(iTime, 5.); // changed music so doesnt work as well now
    drop = smoothstep(0., 1., drop);

    float sc = 20. * tanh(iTime / 5.) + 30. * tanh(iTime / 30.); //idk
    float ix = floor(sc * uv.x) / sc;
    float fx = fract(sc * uv.x) - 0.5;

    float r = texture( iChannel0, vec2(ix, uv.y * 0.)).r;
    float t = texture(iChannel0, vec2(0.25,0)).r; // doesnt change very smoothly

    fx += (0.1 + 0.1 * t) * cos(sc * uv.y * r + 0.12 * r * iTime);

    float d = sdBox(vec2(fx, uv.y), vec2(0.4 - 0.3 * r, r));
    d = min(d, length(vec2(fx, (uv.y-r) *sc / res)) - 0.4 + 0.3 * r);

    float k = 80. * r / iResolution.y;
    float s = smoothstep(-k, k, -d); 

    vec3 e = vec3(0.5);   

    col = s * pal(0.5 * t + r - 0.5 * exp(-2.5 * uv.y) + 1.5, e, e, e, drop * (0.5 * t) * vec3(0,1,2)/3.);
   // col += 0.01 * (1.-s) * pal(80. * exp(-2.5 * uv.y) + iTime, e, e, e, drop * (0.5 * t) * vec3(0,1,2)/3.);
    vec3 col2 = (1.-s) * pal(0.5 * t + 0.5 * exp(-2.5 * uv.y) + 1., e, e, e, drop * 0.2 * vec3(0,1,2)/3.);
    //col = mix(col, col2, s);
    col += (0.5 + 0.5 * t + 0.25 * uv.x) * col2;
    fragColor = vec4(col,1.0);
}
