vec2 tile_uv(vec2 uv, float zoom){
    uv.x *= iResolution.x/iResolution.y;
    uv *= zoom;
    uv.x -= ceil(uv.y)*2./9.;

    return uv;
}

float tile_uv_mask(vec2 uv, float zoom){
    uv.x *= iResolution.x/iResolution.y;
    uv *= zoom;
    return step(1., mod(1.+uv.y, 2.0))*step(1., mod(1.+uv.x,2.0));
}

vec4 draw_prt(vec2 uv, float zoom){
    vec2 uv_wire = tile_uv(uv*vec2(1./9.,1.), zoom);
    vec4 tex1 = texture(iChannel1, uv_wire+vec2(floor(iTime*5.)/9., 1.));
    tex1.a *= tile_uv_mask(uv, zoom)*tile_uv_mask(uv, zoom*0.5);
    return tex1;
}

float random (vec2 st) {
    return fract(
                sin(
                    dot(st.xy,vec2(12.9898,78.233))
                )
           *43758.5453123+iTime);
}

vec4 draw_scratches(vec2 uv, float zoom){
    vec2 uv_wire = tile_uv(uv*vec2(1./9.,1.), zoom);
    uv_wire -= floor(iTime);
    vec4 tex1 = texture(iChannel2, uv_wire+vec2(floor(iTime*5.)/9., 1.));
    tex1.a *= tile_uv_mask(uv, zoom);
    return tex1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 qq = vec2(iResolution.x/iResolution.y, 1.0);

    float zoom = 10.;

    // Output to screen
    vec4 tex0 = texture(iChannel0, uv);
    float rnd = random( uv );
    float rnd2 = random(uv/1.1);
    float rnd3 = random(uv/0.9);

    fragColor = mix(tex0, draw_prt(uv, zoom), draw_prt(uv, zoom).a); // add hearts
    fragColor = mix(fragColor, vec4(vec3(rnd, rnd2, rnd3),1.0), 0.1); // add noise
    vec3 sepia = vec3(
        fragColor.r * .393 + fragColor.g *.769 + fragColor.b * .189,
        fragColor.r * .349 + fragColor.g *.686 + fragColor.b * .168,
		fragColor.r * .272 + fragColor.g *.534 + fragColor.b * .131);
    fragColor = mix(fragColor, vec4(sepia, 1.0), 0.45); // add sepia 
    fragColor = mix(fragColor, vec4(1.), draw_scratches(uv, zoom*0.485).a*0.23);// add white scratches
    fragColor = mix(fragColor, vec4(0.), draw_scratches(uv, zoom*0.368).a*0.153);//add black scratches

    if (uv.x<0.5+0.5*sin(iTime))
        fragColor = tex0;


}
