#define PI 3.1415
#define ASPECT_RATIO iResolution.y / iResolution.x
#define FOV PI / 4.0
#define UP vec3(0.0, 1.0, 0.0)
#define MAX_IT 7
#define INFINITE 9999999999999999.0
#define resolutioni 8.0
#define resolution float(resolutioni)
#define MAX_CHAR 63.0


//x is in front of you, y is up, z is right.

struct Hit
{
    bool hit;
    vec3 pos;
    float t;
    vec3 normal;
    vec3 ray;
    vec3 rgb;
    int mode;
};
struct Camera
{
    vec3 Position;
    vec3 Direction;
};

Hit PlaneHit(vec3 origin, vec3 ray, float height, int mode)
{
    if(ray.y == 0.0){
        if(height == origin.y)
            return Hit(true, origin, 0.0, UP, ray, vec3(0.0, 1.0, 0.0), mode);
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);
    }

    float t = (height - origin.y) / ray.y;
    if(t < 0.0)
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);

    return Hit(true, origin + (t)*ray, t, UP, ray, vec3(1.0), mode);
}
Hit SphereHit(vec3 origin, vec3 ray, vec3 sphpos, float radius, int mode, vec3 color)
{
    float a = dot(ray, ray);
    float b = dot(ray, origin - sphpos);
    float c = dot(origin - sphpos, origin - sphpos) - radius * radius;
    float d = b*b - a*c;
    if(d < 0.0)
    {
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);
    }

    float fr = (-b - sqrt(d))/a;
    float sr = (-b + sqrt(d))/a;

    if(fr < 0.0)
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);

    vec3 n = vec3(origin + fr*ray - sphpos);
    n /= length(n);


    return Hit(true, origin + (fr) * ray, fr, n, ray, color, mode);

}
Hit GetHit(vec3 origin, vec3 ray)
{
    Hit h1 = SphereHit(origin, ray, vec3(8.0 + sin(iTime), cos(iTime), cos(iTime)), 0.9, 0, vec3(1.0, 1.0, 1.0));
    Hit h2 = PlaneHit(origin, ray, -2.0, 0);  
    Hit h3 = SphereHit(origin, ray, vec3(8.0, -1.48, 0.5), 0.5, 0, vec3(1.0, 0.0, 0.0));
    Hit h4 = SphereHit(origin, ray, vec3(6.1, -1.70, -1.5), 0.4, 0, vec3(0.0, 0.7, 0.3));
    Hit h5 = SphereHit(origin, ray, vec3(7.8, -1.78, 1.5), 0.2, 0, vec3(1.0, 0.0, 1.0));
    Hit h6 = SphereHit(origin, ray, vec3(5.5, -0.48, 2.8), 1.5, 0, vec3(1.0, 0.0, 1.0));
    Hit h7 = SphereHit(origin, ray, vec3(12.9, -1.48, -2.5), 0.5, 0, vec3(0.0, 0.1, 0.5));
    Hit h8 = SphereHit(origin, ray, vec3(9.8, -1.5, -1.3), 0.2, 0, vec3(0.0, 0.0, 0.0));
    Hit h9 = SphereHit(origin, ray, vec3(3.8, -1.78, 2.9), 0.2, 0, vec3(1.0, 0.0, 1.0));
    Hit h10 =SphereHit(origin, ray, vec3(6.8, -1.78,-1.5), 0.2, 0, vec3(1.0, 0.0, 1.0));



    Hit HitList[] = Hit[](h1, h2, h3, h4, h5, h6, h7, h8, h9, h10);

    Hit h = Hit(false, vec3(0.0), INFINITE, vec3(0.0), vec3(0.0), vec3(0.0), 0);
    for(int i = 0; i < HitList.length(); i++)
    {
        if(HitList[i].hit && HitList[i].t < h.t)
            h = HitList[i];
    }
    return h;

}
vec4 GetColor(Hit h)
{
    vec3 lightdir = vec3(0.0, -0.7, 0.5 + 0.5*cos(iTime));
    float inten = 1.0;
    float base = 0.5;
    float t = 0.7;
    lightdir /= length(lightdir);
    if(h.hit)
    {
         vec4 c = (t * base + (1.0 - t)*dot(-lightdir, h.normal) * inten) * vec4(h.rgb,0.0);
         vec3 dir = - lightdir;
         vec3 pos = h.pos + dir * 0.005;
         Hit new_hit = GetHit(pos, dir);
           if(new_hit.hit)
               return vec4(0.0);
        return c;
    }
    else{
        float skyi = 1.0;
        return (1.0 - skyi)*vec4(1.0) + skyi*vec4(0.1, 0.7, 1.0, 0.0);
    }
}
vec3 GetRayFromUV(vec3 direction, vec2 uv)
{
    direction /= length(direction);
    vec3 r = direction - uv.x * tan(FOV) * cross(direction, UP) + ASPECT_RATIO * uv.y * UP;
    r /= length(r);
    return r;   
}

vec4 pixel(Camera cam, vec2 uv)
{
    vec3 r = GetRayFromUV(cam.Direction, uv);
    Hit h;
    vec4 color = vec4(0.0);

    vec3 origin = cam.Position;
    vec3 dir = r;

    float intensity = 0.5;

    for(int i = 0; i < MAX_IT; i++)
    {
        Hit h = GetHit(origin, dir);
        if(!h.hit || h.mode == 1)
        {
            color = color + intensity * GetColor(h);
            break;
        }
        if(h.mode == 0){
            dir = reflect(dir, h.normal);
            dir /= length(dir);
            origin = h.pos + dir*0.1;
        }
        if(h.mode == 2)
        {
            dir = reflect(dir, h.normal);
            dir /= length(dir);
            origin = h.pos + dir*0.1;

            Hit new_hit = GetHit(origin, dir);
            color = color + intensity*intensity*intensity * GetColor(new_hit);

            dir = h.ray;
            origin = h.pos + h.ray * 1.1;
        }
        color = color + intensity * GetColor(h);
        intensity = intensity * 0.5;
    }
    return color;
}

vec4 getChar(float intensity, vec2 fragCoord)
{
    vec2 localcoord = mod(fragCoord, resolution);
    intensity = 1.0 - intensity;
    intensity = floor(intensity * MAX_CHAR);

    float char = intensity*resolution;
    float deviation = char;
    vec2 xydev = vec2(mod(deviation, iResolution.x), floor(deviation / iResolution.x) * resolution);

    localcoord += xydev;
    vec2 uv = localcoord / iResolution.xy;

    return texture(iChannel0, uv);


}
vec2 getRepresentative(vec2 fragCoord)
{
    return resolution*(floor(fragCoord / resolution));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 rfragCoord = getRepresentative(fragCoord);

    vec2 uv = 2.0*rfragCoord/iResolution.xy - 1.0;
    Camera cam = Camera(vec3(3.0, -1.0, 0.0), vec3(1.0, 0.0, 0.0));
    vec3 r = GetRayFromUV(cam.Direction, uv);
    vec4 o = pixel(cam, uv);

    float intensity = 0.299 * o.x + 0.587 * o.y + 0.114 * o.z;
    intensity = max(intensity, 0.0);
    fragColor = getChar(intensity, fragCoord) * o * 1.7;

}

#define MAX_CHAR 63
#define RESOLUTION 40.0
#define FACTOR 5.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rfragCoordx = mod(fragCoord.x * FACTOR, iResolution.x); 
    float rfragCoordy = fragCoord.y*FACTOR + RESOLUTION * floor(fragCoord.x * FACTOR / iResolution.x);

    vec2 rfragCoord = vec2(rfragCoordx, rfragCoordy);

    vec2 uv = mod(rfragCoord / iResolution.xy, 1.0);

    fragColor = texture(iChannel0, uv);
}
