void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0.0);
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 uv = (2.0 * fragCoord - iResolution.xy) /iResolution.y;
    vec3 mainColor = vec3(0.4, 0.5, 0.8);

    vec2 st = uv;
    uv.x += sin(st.y * iTime) * cos(st.y + iTime);
    uv.y += cos(st.x * iTime) * sin(st.x - iTime);

    float c = cos(iTime) * 2.0 - 1.0;
    float s = -sin(iTime) * 2.0 - 1.0;
    mat2 rot = mat2
    (
         c, -s, 
         s,  c
    );

    uv *= rot;
    uv.x += sin(iTime) * 2.0 - 1.0;
    uv.y -= cos(iTime) * 2.0 - 1.0;

    uv.x = mod(10.0, abs(uv.x));
    uv.y = mod(10.0, abs(uv.y));

    color = mix(vec3(1.0), mainColor, 1.0 - length(uv)) * (vec3(1.0) - vec3(abs(uv), 0.0));

    fragColor = vec4(color, 1.0);
}
