#define PI 3.14159265
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dist1 = sin(iTime*.003)*1.;
    float fract1 = .15;
    float fract2 = .2;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float c = length(uv);
    uv = vec2(fract(atan(uv.x,uv.y)*PI*fract1+c*dist1), fract(length(uv)*PI*fract2-iTime*.08));
    vec3 col = vec3(uv, 0.);
    col = texture(iChannel0, uv*2.+iTime*.08).rgb;
    col = sin(col*4.);
    col = col.rgb*col.rgg;
    fragColor = vec4(col,1.0);
}
