#define N_ITER  5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    float phi = .5 * 3.1415 + .1*iTime;

    vec3 col = vec3(0.);
    col.rg += sin(10.*3.1415*uv.xy);
    vec2 st = uv;
    for (int i = 0; i < N_ITER && i < int(floor(iTime)); ++i) {
        uv.x = st.x*st.y;
        uv.y = cos(10.*st.x);
        col.rg += sin(10.*3.1415*uv.xy);
    }
    col /= float(N_ITER);


    fragColor = vec4(col,1.0);
}
