float rect(vec2 uv, vec2 pos, vec2 size, float blur){
    float x1 = smoothstep(pos.x-blur, pos.x+blur, uv.x);
    float x2 = smoothstep(pos.x+size.x+blur, pos.x+size.x-blur, uv.x);
    float y1 = smoothstep(pos.y-blur, pos.y+blur, uv.y);
    float y2 = smoothstep(pos.y+size.y+blur, pos.y+size.y-blur, uv.y);
    return x1*x2*y1*y2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 R = iResolution.xy;
    vec3 col = vec3(0.);
    for(int i=0;i<50;i++){
        col += rect(fragCoord, vec2(R.x/50., R.y/50.)*float(i), vec2(R.x/64., R.y/36.), 1.);
        col += rect(fragCoord, vec2(0., R.y-R.y/36.)-vec2(R.x/50.*float(-i), R.y/50.*float(i)), vec2(R.x/64., R.y/36.), 1.);
    }
    col += rect(fragCoord, vec2(R.x/640.*150., R.y/360.*210.), vec2(R.x/640.*20., R.y/360.*35.), sin(t*10.)*5.+6.);

    vec2 pos1 = vec2(sin(t), cos(t))*50.+vec2(R.x/2., R.y/2.);
    col += rect(fragCoord, pos1, vec2(R.x/64., R.y/36.), 1.);

    fragColor = vec4(col, 1.);
}
