// Taken from https://www.shadertoy.com/view/Msf3WH
float hash(vec3 p) { // replace this by something better
  p  = fract( p*0.2183099+.1);
  p *= 17.0;
  return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

vec3 cellCenter( vec3 ic ) {
  return vec3(hash(ic.xyz), hash(ic.yxz * 153.), hash(ic.zxy));
}

float distanceToCenter( vec3 c0, vec3 ic) {
  vec3 fc = c0 - ic;
  vec3 center = cellCenter(ic.xyz);
  vec3 delta = center - fc;
  float r = dot( delta, delta );
  return r;
}

float voronoise3D(vec3 c) {
  float r = 10.;
  float r1 = r;
  vec3 ic = floor(c);
  for( int x=-1; x<=1; x+=1 ) {
      for( int y=-1; y<=1; y+=1 ) {
        for( int z=-1; z<=1; z+=1 ) {
          float nr = distanceToCenter(c, ic + vec3(x,y,z));
          // Keep track of the two nearest distances
          if( nr < r ) {
            r1 = r;
            r = nr;
          } else if( nr < r1 ) {
            r1 = nr;
          }
         }
      }
  }
  //return r1 - r;
  return r * 0.5 + r1 * 0.5;
  return 1.0 - ( r + r1 ) * 0.5;
}

float doNoise( vec3 uv ) {
  float r = voronoise3D( uv );
  return r;
  float r1 = voronoise3D( uv * 2. + vec3(0.5) ) * 0.5;
  return ( r + r1 * r1 );
  float r2 = voronoise3D( uv * 4. ) * 0.25;
  return (r + r1 * r1 + r2 * r2 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * vec2( iResolution.x / iResolution.y, 1 );
    float time_scale = 1.0; // (2.0 + sin( iTime));
    float n = doNoise( vec3( time_scale * uv * 8., iTime * 0.1 ));

    // Output to screen
    fragColor = vec4(n,n,n,1.0);
}
