void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float density = 10.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

    vec2 checker_2d = mod(uv * density, 1.);
    vec2 checker_bool = floor(mod(uv * density, 2.));
    float checker = mod(checker_bool.x + checker_bool.y, 2.);
    float checker_v = abs(checker_2d.x - 0.5) > 0.3 ? 0. : checker;
    float checker_h = abs(checker_2d.y - 0.5) > 0.3 ? 0. : 1. - checker;
    fragColor = vec4(checker_v, checker_h, 0., 1.);
}
