void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;



   // Time varying pixel color
   // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float x = length(vec2(uv.x*1.667, uv.y) - vec2(0.83, 0.5)) + 0.5*sin((uv.y + iTime) * 4.0) * 0.038 + 0.3*sin((uv.y + iTime) * 8.0) * 0.01 + 0.05*sin((uv.y + iTime) * 40.0) * 0.05;   

    float y = 0.7*sin((uv.x + iTime) * 4.0) * 0.038 + 0.3*sin((uv.x + iTime) * 8.0) * 0.010 + 0.05*sin((uv.x + iTime) * 40.0) * 0.05;
    fragColor = texture(iChannel0, 0.79*(uv + vec2(y+0.11, x+0.11)));
    // Output to screen
    if(x<0.3)
    {

    fragColor = vec4(1.0,0, 0, 1.0);
    }
    else
    {
    fragColor = vec4(1.0,1.0, 1.0, 1.0);
    }  
}
