{
    vec2 uv = fragCoord/iResolution.xy; 

    float balanco=(sin(iTime+(uv.x*4.0))/20.0);  
    float vel= 2.0;
    float freq = 20.0;   
    float timeshift = (iTime * vel) + (uv.x * freq) + uv.y;    
    float wiggle = sin(iTime*2.0+(uv.y*5.0))*0.04;    
    float shadow = 0.5 + cos(timeshift) * 0.2 ;

    if(uv.y<0.05+balanco){} 
    else if(uv.x<0.1+balanco){}
    else if(uv.x>0.9+balanco){}
    else if(uv.y>0.8+balanco){}
    else   if(uv.y<0.3+balanco){
        fragColor = vec4(5,0,0,0)*1.0-balanco*5.0 +shadow;
    }else if(uv.y>0.55+balanco){
