void mainImage( out vec4 O, in vec2 I)
{

    I = (I+I-iResolution.xy)/iResolution.y;

    float f = 20.;  
    float pixel = 0.;

    I = sin(I*f);

    /*vertical checkers at step(0., _)*/
    pixel = step(sin(iTime), I.x*I.y);

    /*oblique checkers at step(0.,_)*/
    //pixel = step(sin(iTime)*2., I.x+I.y);

    /*oblique checkers at step(0._), with max = sqrt(2)*/
    //pixel = step(abs(sin(iTime/2.)*sqrt(2.)), length(I));

    /*oblique checkers at step(0., dot(I, vec2(1.,1.)))*/
    //pixel = step(0., dot(I, vec2(sin(iTime), cos(iTime))));

    /*
            You can uncomment all of them at the same time and 
        change the '=' to an '+=' or an '*=' for interesting effects.
    */

    O = vec4(pixel);

}
