// Jonas Norberg 2022

vec2 h = vec2(0.5,0.5);

vec2 slice9sample( vec2 x, vec2 t )
{
    vec2 i = floor( x + h ) - h;
    vec2 f = fract( x + h ) - h;

    f *= t;
    f += h;
    f = clamp( f,0.0 , 1.0 );

    return i+f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Animate the "border width"
    // This mul value is basically TotalWidth / BorderWidth
    vec2 mul = vec2(2.0+0.5*sin(iTime),2.6+0.5*cos(iTime));

    // Distort the uv-coordinates
    uv = slice9sample( uv, mul );

    // Sample texture once
    vec4 col = texture ( iChannel0, uv );

    // Output to screen
    fragColor = col;
 }
