void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t=iTime*iTime,d=sin(pow(uv.x,.88)*30.+t),d2=max(0.,sin(pow(uv.x,1.2)*40.-t));

    // Time varying pixel color
    vec3 col = vec3(d>0.?d:d2);

    if(iTime>100.&&(iTime>128.||fract(t)<.1))col=vec3(0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float song(float time){

  float n=floor(time*5.),n1=n;

  n=mod(n*n,256.);

  if(n>=64.)n=12.+mod(n,64.);

  return pow(2.,n/12.+6.+floor(n1/64.));

}

vec2 mainSound(int samp, float time){

  vec2 a=vec2(0);

  for(float i=0.;i<8.;i+=1.){

    float n=song(time-i*i*.125);

    a+=vec2(pow(fract(n*time),1./(1.+i))-.5)/(1.+i);

  }

  return a;

}
