void mainImage( out vec4 f, in vec2 w )
{
	vec2 r = iResolution.xy, p = w - r*.5;
    vec4 s = textureLod(iChannel0,w,2.);
	float d = length(p*1.5) / r.y , a = atan(p.x, p.y)/6.283, b = length(s)/2.,
    e = pow(d*b,.15), v = .3/mod(floor((e+.15)*10.), (8.5+b) * b),
    c = abs(mod(a + iTime*v*v*4.*b - d, .1)/.02 - 2.) * abs(mod(e, .1)/.07 - .5); d+=.2;
	f = (vec4(c/b,c*d*d*b,c-d*sin(iTime/10.)/2.,1)*(d+.5)*s.rrra).bbra;
}
