#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

struct complex{
 vec2 v;
};

vec2 mul(vec2 v1, vec2 v2){
 return vec2(v1.y*v2.y - v1.x*v2.x,
         v1.x*v2.y + v2.x*v1.y);
}
void mainImage( out vec4 FragColor, in vec2 FragCoord )
{
 vec2 uv = (FragCoord.xy -.5*iResolution.xy) / iResolution.y;
 vec2 ms = (iMouse.xy-.5*iResolution.xy) / iResolution.y;
  uv *= 2.0*(2. + sin(iTime));
  ms = vec2(tan(uv));
  vec3 col = vec3(0);

 complex point;
 point.v = vec2( uv );

 for(int i = 0; i < 3; i++){
  point.v = mul(point.v, mul(point.v, point.v) );
  point.v += ms;
 }

 uv = step(vec2(0), uv);
 if(iFrame <= 2)
  col = vec3(uv, 0.5);
 else
  col = texture(iChannel0, point.v).rgb;
 FragColor = vec4(col, 1.0);
}
