// Inspired by Est√∫dio Vaca Roxa's Generic RPG pack[1]
// [1]: https://bakudas.itch.io/generic-rpg-pack
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 gridSize = vec2(3,3);
    vec2 gridOffset = vec2(0, 0);
    vec2 gridWithPadding = 2.0 * gridSize - vec2(1,1);
    float value = 5.7;

    vec2 gridPosition = uv * gridWithPadding + gridOffset;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col;
    if (mod(floor(gridPosition), 2.0) == vec2(0, 0)) {
      float cellNormY = mod(gridPosition.y/2.0, 1.0) * 2.0; ///2.0 - floor(gridPosition.y/2.0);
      float gridValue = floor(gridPosition.x/2.0) + 1.0 + floor(gridPosition.y/2.0) * gridSize.x + cellNormY;
      if (gridValue <= value)
        col = vec3(0, 1, 0);
      else
        col = vec3(0, 0.4, 0);
    } else
      col = vec3(0, 0, 0);
    // Output to screen
    fragColor = vec4(col,1.0);
}
