#define TIME mod(iTime/2.0,10.0)

//float st(float s) { return smoothstep(0.99-0.05/texture(iChannel0,vec2(0.5,0.0)).x,1.0,s); }
float st(float s) { return smoothstep(0.99,1.0,s); }
//float st(float s) { return smoothstep(-2.0+(smoothstep(0.0,3.0,TIME)-smoothstep(6.0,9.0,TIME))*2.99,1.0,s); }

vec3 brazil(vec2 uv) {
	vec3 col = vec3(0.3,0.6,0.0);
	col = mix(col,vec3(0.9,0.8,0.0),1.0-st(abs(uv.x/1.6)+abs(uv.y)+0.25));
	col = mix(col,vec3(0.0,0.0,0.25),1.0-st(length(uv)+0.5));
	col = mix(col,vec3(1.0),st(1.0-abs(uv.y-0.15+pow(uv.x/1.5+0.15,2.0))+0.04)*(1.0-st(length(uv)+0.5)));
	col *= 1.0-st(abs(uv.y));
	col *= 1.0-st(abs(uv.x)-0.6);
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

	float x = uv.x*2.0+iTime*1.5;

	uv.y *= 1.0+sin(x)*0.05;

	vec3 col = brazil(uv*1.25)*(3.0-sin(x))/3.0;

	fragColor = vec4(col,1.0);
}
